    <style>
        .centerd_image {
            margin: auto;
            display: flex;
            justify-content: center;
            align-items: center;
            /* height: 100vh; */
        }

        img {
            width: 40%;
            height: 20%;
        }

        .header {
            border: solid transparent;
            background-color: #a0adad;
            color: black;
            width: 100%;
            text-align: center;
            padding: 50px;
            border-radius: 25px;
            margin: 30px;
        }

        .silver-background {
            background-color: silver;
            border-radius: 30px;
        }

        .center {
            margin: auto;
            width: 87%;
            text-align: center;
            padding: 30px;
        }

        .code {
            border: solid transparent;
            padding: 25px;
            background-color: silver;
            color: black;
        }
    </style>
